// /frontend/src/components/Sidebar.jsx

import React from 'react';
import { X } from 'lucide-react';

const Sidebar = ({ isOpen, setIsOpen, conversations, activeConversationId, setActiveConversationId, createNewConversation }) => {

    const handleNewChat = () => {
        // This wrapper function ensures createNewConversation is called with no arguments,
        // preventing the event object from being passed as the title.
        createNewConversation();
        setIsOpen(false); // Also close the sidebar for a better user experience
    };

    return (
        <div className={`absolute top-0 left-0 h-full w-72 bg-gray-100 dark:bg-gray-900 border-r border-gray-200 dark:border-gray-800 transform ${isOpen ? 'translate-x-0' : '-translate-x-full'} transition-transform duration-300 ease-in-out z-30 flex flex-col p-4`}>
            <div className="flex justify-between items-center mb-4">
                <h2 className="text-lg font-bold text-gray-800 dark:text-gray-200">History</h2>
                <button onClick={() => setIsOpen(false)} className="p-1 rounded-md hover:bg-gray-200 dark:hover:bg-gray-700">
                    <X size={20} />
                </button>
            </div>
            <button onClick={handleNewChat} className="w-full mb-4 bg-indigo-600 hover:bg-indigo-500 text-white font-semibold py-2 px-4 rounded-lg transition-colors">
                + New Chat
            </button>
            <div className="flex-grow overflow-y-auto">
                {Object.values(conversations).reverse().map(conv => (
                    <button 
                        key={conv.id} 
                        onClick={() => {
                            setActiveConversationId(conv.id);
                            setIsOpen(false);
                        }}
                        className={`w-full text-left p-2 my-1 rounded-md truncate ${activeConversationId === conv.id ? 'bg-gray-300 dark:bg-gray-700' : 'hover:bg-gray-200 dark:hover:bg-gray-800'}`}
                    >
                        {conv.title}
                    </button>
                ))}
            </div>
        </div>
    );
};

export default Sidebar;
