import React, { useState, useRef, useEffect } from 'react';
import { LineChart, Line, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { UploadCloud, Send, Bot, User, FileText, BarChart2, Loader2, Sparkles } from 'lucide-react';

// --- Re-styled Helper Components ---

const ChatMessage = ({ msg }) => {
  const isBot = msg.sender === 'bot';
  const colors = ["#8884d8", "#82ca9d", "#ffc658", "#ff8042", "#0088FE", "#00C49F"];

  return (
    <div className={`flex items-start gap-4 w-full`}>
      <div className={`w-8 h-8 rounded-full bg-gray-700 flex items-center justify-center flex-shrink-0 text-white`}>
        {isBot ? <Bot size={20} /> : <User size={20} />}
      </div>
      <div className="flex-1 pt-1">
        <p className="font-bold text-gray-200">{isBot ? 'Gemini' : 'You'}</p>
        <div className="mt-1 text-gray-300 text-base leading-relaxed">
          <p className="whitespace-pre-wrap">{msg.text}</p>
          {msg.type === 'chart' && msg.chartData && (
            <div className="mt-4 p-4 bg-gray-950 border border-gray-700 rounded-lg w-full h-[350px] text-white animate-fade-in">
              <ResponsiveContainer width="100%" height="100%">
                <LineChart data={msg.chartData} margin={{ top: 5, right: 20, left: -10, bottom: 5 }}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#4A5568" />
                  <XAxis dataKey="name" stroke="#A0AEC0" tick={{ fill: '#A0AEC0', fontSize: 12 }} />
                  <YAxis stroke="#A0AEC0" tick={{ fill: '#A0AEC0', fontSize: 12 }}/>
                  <Tooltip 
                    contentStyle={{ backgroundColor: 'rgba(31, 41, 55, 0.8)', border: '1px solid #4A5568', backdropFilter: 'blur(4px)' }} 
                    labelStyle={{ color: '#E5E7EB' }}
                  />
                  <Legend wrapperStyle={{fontSize: "14px"}} />
                  {msg.dataKeys && msg.dataKeys.map((key, index) => (
                    <Line key={key} type="monotone" dataKey={key} stroke={colors[index % colors.length]} strokeWidth={2} activeDot={{ r: 8 }} />
                  ))}
                </LineChart>
              </ResponsiveContainer>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const LoadingIndicator = () => (
  <div className="flex items-start gap-4 w-full">
    <div className="w-8 h-8 rounded-full bg-gray-700 flex items-center justify-center flex-shrink-0">
      <Bot size={20} className="text-white" />
    </div>
    <div className="flex-1 pt-1 flex items-center gap-3">
       <Loader2 className="animate-spin text-gray-400" size={20} />
       <span className="text-sm text-gray-400">Thinking...</span>
    </div>
  </div>
);


// --- Main App Component ---

const App = () => {
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const chatEndRef = useRef(null);
  const fileInputRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isLoading]);

  useEffect(() => {
    // This effect now only runs if there are no messages, to avoid re-adding the welcome message.
    if (messages.length === 0) {
      setMessages([{
        sender: 'bot',
        text: 'Hello! I am an intelligent assistant for data analysis. Please upload your CSV or Parquet file to begin.',
        type: 'text'
      }]);
    }
  }, [messages.length]);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setFileName(selectedFile.name);
      setMessages(prev => [...prev, {
        sender: 'bot',
        text: `Successfully loaded "${selectedFile.name}".\n\nHow can I help you analyze this data?`,
        type: 'text'
      }]);
    }
  };

  const handleSendMessage = async () => {
    if (!input.trim() || isLoading || !file) return;

    const userMessage = { sender: 'user', text: input, type: 'text' };
    setMessages(prev => [...prev, userMessage]);
    const currentInput = input;
    setInput('');
    setIsLoading(true);

    const formData = new FormData();
    formData.append('file', file);
    formData.append('query', currentInput);

    try {
      const response = await fetch('http://localhost:5001/api/chat', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const err = await response.json();
        throw new Error(err.error || `HTTP error! status: ${response.status}`);
      }

      const botResponse = await response.json();
      setMessages(prev => [...prev, botResponse]);

    } catch (error) {
      console.error("Error fetching from backend:", error);
      const errorResponse = {
        sender: 'bot',
        text: `Sorry, an error occurred: ${error.message}. Please ensure the backend server is running correctly.`,
        type: 'text'
      };
      setMessages(prev => [...prev, errorResponse]);
    } finally {
      setIsLoading(false);
    }
  };

  const WelcomeScreen = () => (
    <div className="flex flex-col items-center justify-center h-full text-center">
        <div className="p-4 bg-gray-700 rounded-full mb-6">
            <Sparkles size={40} className="text-indigo-400"/>
        </div>
        <h1 className="text-4xl font-bold text-gray-200">Data Insights Bot</h1>
        <p className="mt-4 text-lg text-gray-400">Upload a file to start your analysis.</p>
        <button 
            onClick={() => fileInputRef.current && fileInputRef.current.click()}
            className="mt-8 bg-indigo-600 hover:bg-indigo-500 text-white font-semibold py-3 px-6 rounded-lg inline-flex items-center justify-center transition-colors shadow-lg"
        >
            <UploadCloud className="mr-2" />
            <span>Select File</span>
        </button>
    </div>
  );

  return (
    <div className="flex h-screen bg-gray-900 text-gray-200 font-sans antialiased">
      <input 
        id="file-upload" 
        type="file" 
        className="hidden" 
        onChange={handleFileChange} 
        accept=".csv,.parquet"
        ref={fileInputRef}
      />
      <main className="flex-1 flex flex-col items-center relative">
        {/* Header */}
        <header className="w-full max-w-4xl p-4 flex justify-between items-center">
            <h1 className="text-lg font-semibold text-gray-300">Data Insights Bot</h1>
            {fileName && (
                <div className="p-2 px-3 bg-gray-700 border border-gray-600 rounded-lg text-sm flex items-center gap-2">
                    <FileText size={16} className="text-green-400" />
                    <span className="font-medium text-gray-300 truncate">{fileName}</span>
                </div>
            )}
        </header>

        {/* Chat Area */}
        <div className="flex-1 w-full max-w-4xl overflow-y-auto px-4 pb-24">
          <div className="flex flex-col space-y-8">
            {messages.length <= 1 && !file ? (
                <WelcomeScreen />
            ) : (
                messages.map((msg, index) => <ChatMessage key={index} msg={msg} />)
            )}
            {isLoading && <LoadingIndicator />}
            <div ref={chatEndRef} />
          </div>
        </div>

        {/* Message Input */}
        <div className="absolute bottom-0 w-full flex justify-center p-6 bg-gradient-to-t from-gray-900 to-transparent">
            <div className="w-full max-w-4xl">
                <div className="flex items-center bg-gray-800 border border-gray-700 rounded-xl p-2 shadow-2xl">
                    <textarea
                        rows={1}
                        className="flex-1 bg-transparent border-none focus:ring-0 text-white placeholder-gray-500 px-4 resize-none"
                        placeholder={file ? "Ask about your data..." : "Please upload a file to begin"}
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && !e.shiftKey && (e.preventDefault(), handleSendMessage())}
                        disabled={!file || isLoading}
                    />
                    <button
                        onClick={handleSendMessage}
                        className="bg-indigo-600 hover:bg-indigo-500 disabled:bg-gray-600 disabled:cursor-not-allowed text-white font-bold p-3 rounded-lg transition-colors"
                        disabled={!file || isLoading || !input.trim()}
                    >
                        <Send />
                    </button>
                </div>
                <p className="text-xs text-center text-gray-500 mt-3">
                    Data Insights Bot can make mistakes. Consider checking important information.
                </p>
            </div>
        </div>
      </main>
    </div>
  );
};

export default App;
