// /frontend/src/components/ChatInterface.jsx

import React, { useState, useRef, useEffect } from 'react';
import { useTheme } from '../contexts/ThemeContext';
import { useConversations } from '../hooks/useConversations';
import ChatMessage from './ChatMessage';
import WelcomeScreen from './WelcomeScreen';
import Sidebar from './Sidebar';
import Header from './Header';
import MessageInput from './MessageInput';

const ChatInterface = () => {
  const { theme, toggleTheme } = useTheme();
  const { 
    conversations, 
    activeConversationId, 
    setActiveConversationId, 
    createNewConversation,
    addMessageToConversation,
    updateConversationTitle,
    updateConversationFile
  } = useConversations();

  // This state will hold the actual File object in memory, but will not be persisted.
  const [activeFile, setActiveFile] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isHistoryOpen, setIsHistoryOpen] = useState(false);
  const chatEndRef = useRef(null);
  const fileInputRef = useRef(null);

  const activeConversation = conversations[activeConversationId];
  const messages = activeConversation?.messages || [];

  useEffect(() => {
    // When the active conversation changes, clear the active file.
    // The user will be prompted to upload a file if they want to chat.
    setActiveFile(null);
  }, [activeConversationId]);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isLoading]);

  const handleFileChange = (file) => {
    if (file) {
      const newConvId = createNewConversation(`Analysis of ${file.name}`);
      updateConversationFile(newConvId, file);
      setActiveFile(file); // Keep the actual file object in state
    }
  };

  const handleSendMessage = async (input) => {
    if (!input.trim() || isLoading || !activeFile) {
        if (!activeFile) {
            alert("Please re-upload the file for this conversation to continue.");
        }
        return;
    }

    addMessageToConversation(activeConversationId, { sender: 'user', text: input, type: 'text' });
    setIsLoading(true);

    const formData = new FormData();
    formData.append('file', activeFile); // Use the file from state
    formData.append('query', input);
    formData.append('conversation_id', activeConversationId);

    try {
      const response = await fetch('http://localhost:8000/api/chat', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const err = await response.json();
        throw new Error(err.detail || `HTTP error!`);
      }

      const botResponse = await response.json();
      addMessageToConversation(activeConversationId, botResponse);

    } catch (error) {
      addMessageToConversation(activeConversationId, { sender: 'bot', text: `Sorry, an error occurred: ${error.message}.`, type: 'text' });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className={`flex h-screen bg-white dark:bg-gray-950 text-gray-800 dark:text-gray-200 font-sans antialiased`}>
      <input 
        id="file-upload" 
        type="file" 
        className="hidden" 
        onChange={(e) => handleFileChange(e.target.files[0])} 
        accept=".csv,.parquet"
        ref={fileInputRef}
      />
      <Sidebar 
        isOpen={isHistoryOpen}
        setIsOpen={setIsHistoryOpen}
        conversations={conversations}
        activeConversationId={activeConversationId}
        setActiveConversationId={setActiveConversationId}
        createNewConversation={createNewConversation}
      />
      <main className="flex-1 flex flex-col items-center relative">
        <Header 
          title={activeConversation?.title}
          fileName={activeConversation?.fileName} // Show the stored file name
          onHistoryClick={() => setIsHistoryOpen(true)}
          onThemeToggle={toggleTheme}
          theme={theme}
        />
        <div className="flex-1 w-full max-w-4xl overflow-y-auto px-4 pb-24 pt-8">
          <div className="flex flex-col space-y-8">
            {messages.length > 0 ? messages.map((msg, index) => <ChatMessage key={msg.id || index} msg={msg} />) : <WelcomeScreen onUploadClick={() => fileInputRef.current.click()} />}
            {isLoading && <ChatMessage.Loading />}
            <div ref={chatEndRef} />
          </div>
        </div>
        <MessageInput onSendMessage={handleSendMessage} isLoading={isLoading} hasFile={!!activeFile} onUploadClick={() => fileInputRef.current.click()} />
      </main>
    </div>
  );
};

export default ChatInterface;
