import React from 'react';
import { Trash2 } from 'lucide-react';

const Sidebar = ({
  isOpen,
  setIsOpen,
  conversations,
  activeConversationId,
  setActiveConversationId,
  createNewConversation,
  deleteConversation,
}) => (
  <aside className={`w-72 bg-gray-200 dark:bg-gray-800 border-r dark:border-gray-900 flex flex-col transition-all duration-300 z-10 ${isOpen ? '' : 'hidden md:block'}`}>
    <div className="flex items-center justify-between px-3 py-4 border-b border-gray-300 dark:border-gray-700">
      <span className="text-lg font-bold">Conversations</span>
      <button
        className="p-1 rounded hover:bg-gray-300 dark:hover:bg-gray-700"
        onClick={createNewConversation}
        title="New Conversation"
      >+</button>
    </div>
    <div className="flex-1 overflow-y-auto">
      {Object.values(conversations).map((conv) => (
        <div key={conv.id} className="flex items-center justify-between px-3 py-2 hover:bg-gray-300/50 dark:hover:bg-gray-700/50 rounded-lg group">
          <div
            className={`truncate cursor-pointer ${conv.id === activeConversationId ? 'font-semibold text-blue-700 dark:text-blue-300' : ''}`}
            onClick={() => setActiveConversationId(conv.id)}
            title={conv.title}
          >
            {conv.title}
          </div>
          <button
            className="ml-2 text-red-500 opacity-0 group-hover:opacity-100 transition-opacity"
            onClick={() => deleteConversation(conv.id)}
            title="Delete chat"
            tabIndex={-1}
          >
            <Trash2 size={16}/>
          </button>
        </div>
      ))}
    </div>
  </aside>
);

export default Sidebar;
