// /frontend/src/components/Sidebar.jsx

import React from 'react';
import { PlusCircle, MessageSquare } from 'lucide-react';

const Sidebar = ({
  isOpen,
  setIsOpen,
  conversations,
  activeConversationId,
  setActiveConversationId,
  createNewConversation,
}) => {
  const conversationList = Object.values(conversations).sort((a, b) => b.id.localeCompare(a.id));

  const handleSelectConversation = (id) => {
    setActiveConversationId(id);
    if (window.innerWidth < 1024) { // Close sidebar on selection on mobile
      setIsOpen(false);
    }
  };

  return (
    <>
      {/* Overlay for mobile */}
      <div
        className={`fixed inset-0 bg-black bg-opacity-50 z-30 transition-opacity lg:hidden ${
          isOpen ? 'opacity-100' : 'opacity-0 pointer-events-none'
        }`}
        onClick={() => setIsOpen(false)}
      ></div>

      <aside
        className={`absolute lg:static top-0 left-0 h-full bg-gray-50 dark:bg-gray-900 border-r border-gray-200 dark:border-gray-800 flex flex-col w-72 transition-transform transform ${
          isOpen ? 'translate-x-0' : '-translate-x-full'
        } lg:translate-x-0 z-40`}
      >
        <div className="p-4 border-b border-gray-200 dark:border-gray-800">
          <h2 className="text-2xl font-bold">OFM Sales AI</h2>
        </div>
        <div className="p-4">
          <button
            onClick={() => createNewConversation("New Analysis")}
            className="w-full flex items-center justify-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
          >
            <PlusCircle size={20} />
            New Analysis
          </button>
        </div>
        <nav className="flex-1 overflow-y-auto p-4 space-y-2">
          <h3 className="px-2 text-sm font-semibold text-gray-500 dark:text-gray-400 uppercase">
            History
          </h3>
          {conversationList.map((conv) => (
            <a
              key={conv.id}
              href="#"
              onClick={(e) => {
                e.preventDefault();
                handleSelectConversation(conv.id);
              }}
              className={`flex items-center gap-3 p-3 rounded-lg truncate transition-colors ${
                activeConversationId === conv.id
                  ? 'bg-blue-100 dark:bg-blue-900/50 text-blue-700 dark:text-blue-300'
                  : 'hover:bg-gray-200 dark:hover:bg-gray-800'
              }`}
            >
              <MessageSquare size={18} className="flex-shrink-0" />
              <span className="flex-grow truncate">{conv.title}</span>
            </a>
          ))}
        </nav>
      </aside>
    </>
  );
};

export default Sidebar;
