import React from 'react';
import { Trash2, X, ChevronRight, ChevronLeft } from 'lucide-react';

const Sidebar = ({
  isOpen,
  setIsOpen,
  conversations,
  activeConversationId,
  setActiveConversationId,
  createNewConversation,
  deleteConversation,
}) => {
  return (
    <>
      {/* Overlay behind sidebar for mobile / closed on clicking outside */}
      <div
        className={`fixed inset-0 z-40 bg-black/50 transition-opacity duration-300 pointer-events-none md:pointer-events-auto ${
          isOpen ? 'opacity-100 pointer-events-auto' : 'opacity-0 pointer-events-none'
        }`}
        onClick={() => setIsOpen(false)}
      />

      {/* Sidebar Panel */}
      <aside
        className={`
          fixed top-0 left-0 z-50 h-full w-72 bg-gray-200 dark:bg-gray-800 border-r dark:border-gray-900 flex flex-col shadow-lg
          transform transition-transform duration-300 ease-in-out
          ${isOpen ? 'translate-x-0' : '-translate-x-full'}
        `}
        role="dialog"
        aria-modal="true"
        aria-hidden={!isOpen}
      >
        <div className="flex items-center justify-between px-4 py-4 border-b border-gray-300 dark:border-gray-700">
          <span className="text-lg font-bold text-gray-800 dark:text-gray-100">Conversations</span>

          <div className="flex items-center gap-2">
            <button
              type="button"
              className="p-1 rounded hover:bg-gray-300 dark:hover:bg-gray-700"
              onClick={() => createNewConversation()}
              title="New Conversation"
            >
              +
            </button>
            <button
              type="button"
              className="p-1 rounded hover:bg-gray-300 dark:hover:bg-gray-700"
              onClick={() => setIsOpen(false)}
              title="Close Conversations"
            >
              <X size={20} color="currentColor" />
            </button>
          </div>
        </div>

        <div className="flex-1 overflow-y-auto">
          {Object.values(conversations).map((conv) => (
            <div
              key={conv.id}
              className={`flex items-center justify-between px-4 py-2 cursor-pointer hover:bg-gray-300/50 dark:hover:bg-gray-700/50 rounded-lg group ${
                conv.id === activeConversationId
                  ? 'font-semibold text-blue-700 dark:text-blue-400'
                  : 'text-gray-800 dark:text-gray-300'
              }`}
              onClick={() => {
                setActiveConversationId(conv.id);
                setIsOpen(false);
              }}
              title={typeof conv.title === 'string' ? conv.title : ''}
            >
              <div className="truncate w-full">
                {typeof conv.title === 'string' ? conv.title : ''}
              </div>
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  deleteConversation(conv.id);
                }}
                title="Delete Conversation"
                className="ml-2 text-red-500 opacity-0 group-hover:opacity-100 transition-opacity"
                tabIndex={-1}
                type="button"
              >
                <Trash2 size={16} />
              </button>
            </div>
          ))}
        </div>
      </aside>

      {/* Persistent Sidebar Toggle Button (left edge) */}
      <button
        aria-label={isOpen ? "Close sidebar" : "Open sidebar"}
        title={isOpen ? "Close sidebar" : "Open sidebar"}
        onClick={() => setIsOpen(!isOpen)}
        className={`
          fixed top-1/3 left-0 z-50 flex h-12 w-8 cursor-pointer items-center justify-center rounded-r-md
          border border-gray-300 bg-white dark:border-gray-700 dark:bg-gray-900
          shadow-md text-gray-700 dark:text-gray-300
          hover:bg-gray-100 dark:hover:bg-gray-800 transition-colors
          focus:outline-none focus:ring-2 focus:ring-blue-500
          ${isOpen ? 'rotate-180' : ''}
        `}
      >
        {isOpen ? <ChevronLeft size={20} /> : <ChevronRight size={20} />}
      </button>
    </>
  );
};

export default Sidebar;
