import React from 'react';

const Header = ({ title, onHistoryClick, onThemeToggle, theme }) => (
  <header className="flex items-center justify-between p-4 border-b bg-white dark:bg-gray-900 dark:border-gray-700">
    <div className="flex items-center gap-4">
      {/* Sidebar toggle button - visible only on small screens */}
      <button
        type="button"
        onClick={onHistoryClick}
        className="md:hidden p-2 rounded hover:bg-gray-200 dark:hover:bg-gray-700"
        aria-label="Open Conversations Sidebar"
      >
        {/* Hamburger Icon SVG */}
        <svg
          className="w-6 h-6 text-gray-800 dark:text-gray-100"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
        </svg>
      </button>
      <h1 className="text-xl font-semibold truncate max-w-xs">{title}</h1>
    </div>
    {/* Theme toggle button */}
    <button
      type="button"
      onClick={onThemeToggle}
      className="p-2 rounded hover:bg-gray-200 dark:hover:bg-gray-700"
      aria-label="Toggle Theme"
    >
      {theme === 'dark' ? '‚òÄÔ∏è' : 'üåô'}
    </button>
  </header>
);

export default Header;
