// /frontend/src/components/ChatInterface.jsx

import React, { useState, useRef, useEffect, useMemo } from 'react';
import { useTheme } from '../contexts/ThemeContext';
import { useConversations } from '../hooks/useConversations';
import ChatMessage from './ChatMessage';
import WelcomeScreen from './WelcomeScreen';
import Sidebar from './Sidebar';
import Header from './Header';
import MessageInput from './MessageInput';

const ChatInterface = () => {
  const { theme, toggleTheme } = useTheme();
  const { 
    conversations, 
    activeConversationId, 
    setActiveConversationId, 
    createNewConversation,
    addMessageToConversation,
    updateConversationTitle,
    updateConversationFile
  } = useConversations();

  // This state holds a map of conversation IDs to their File objects for the current session.
  // This is the key to fixing the state management issues.
  const [fileCache, setFileCache] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [isHistoryOpen, setIsHistoryOpen] = useState(false);
  const chatEndRef = useRef(null);
  const fileInputRef = useRef(null);

  const activeConversation = conversations[activeConversationId];
  const messages = useMemo(() => activeConversation?.messages || [], [activeConversation]);

  // Get the file for the current conversation from our session cache.
  const activeFile = fileCache[activeConversationId];

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages.length, isLoading]);

  const handleFileChange = (file) => {
    if (file) {
      const newConvId = createNewConversation(`Analysis of ${file.name}`);
      updateConversationFile(newConvId, file.name); // Store file NAME in localStorage
      // Store the actual FILE object in the session state, keyed by the new conversation ID
      setFileCache(prevCache => ({ ...prevCache, [newConvId]: file })); 
    }
  };

  const handleSendMessage = async (input) => {
    if (!input.trim() || isLoading) return;

    // If there's no file for this conversation in our session cache, prompt the user.
    if (!activeFile) {
        addMessageToConversation(activeConversationId, {
            sender: 'bot',
            text: "It looks like the data file for this conversation isn't loaded in this session. Please click the paperclip icon to re-upload it and continue your analysis.",
            type: 'error' 
        });
        return;
    }

    const userMessage = { sender: 'user', text: input, type: 'text' };

    const historyForAPI = [...messages, userMessage].map(msg => ({
        role: msg.sender === 'bot' ? 'assistant' : 'user',
        content: msg.text
    }));

    addMessageToConversation(activeConversationId, userMessage);

    if (messages.length === 1) {
        updateConversationTitle(activeConversationId, input);
    }

    setIsLoading(true);

    const formData = new FormData();
    formData.append('file', activeFile);
    formData.append('query', input);
    formData.append('conversation_id', activeConversationId);
    formData.append('history', JSON.stringify(historyForAPI)); 

    try {
      const response = await fetch('http://localhost:5001/api/chat', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const err = await response.json();
        throw new Error(err.detail || `HTTP error!`);
      }

      const botResponse = await response.json();
      addMessageToConversation(activeConversationId, botResponse);

    } catch (error) {
      addMessageToConversation(activeConversationId, { sender: 'bot', text: `Sorry, an error occurred: ${error.message}.`, type: 'text' });
    } finally {
      setIsLoading(false);
    }
  };

  // This function is for when a user needs to re-upload a file for an existing conversation
  const handleReUpload = (file) => {
      if (file && activeConversationId) {
          // Add the re-uploaded file to the cache for the current conversation
          setFileCache(prevCache => ({...prevCache, [activeConversationId]: file}));
          addMessageToConversation(activeConversationId, {
              sender: 'bot',
              text: `Re-loaded "${file.name}". You can now continue your analysis.`,
              type: 'text'
          });
      }
  };


  return (
    <div className={`flex h-screen bg-white dark:bg-gray-950 text-gray-800 dark:text-gray-200 font-sans antialiased`}>
      <input 
        id="file-upload" 
        type="file" 
        className="hidden" 
        onChange={(e) => {
            // If the current conversation already has a file name, we are re-uploading.
            // Otherwise, it's a new file for a new conversation.
            if (activeConversation?.fileName) {
                handleReUpload(e.target.files[0]);
            } else {
                handleFileChange(e.target.files[0]);
            }
        }} 
        accept=".csv,.parquet"
        ref={fileInputRef}
      />
      <Sidebar 
        isOpen={isHistoryOpen}
        setIsOpen={setIsHistoryOpen}
        conversations={conversations}
        activeConversationId={activeConversationId}
        setActiveConversationId={setActiveConversationId}
        createNewConversation={createNewConversation}
      />
      <main className="flex-1 flex flex-col items-center relative">
        <Header 
          title={activeConversation?.title}
          fileName={activeConversation?.fileName}
          onHistoryClick={() => setIsHistoryOpen(true)}
          onThemeToggle={toggleTheme}
          theme={theme}
        />
        <div className="flex-1 w-full max-w-4xl overflow-y-auto px-4 pb-24 pt-8">
          <div className="flex flex-col space-y-8">
            {activeConversation ? messages.map((msg) => <ChatMessage key={msg.id} msg={msg} />) : <WelcomeScreen onUploadClick={() => fileInputRef.current.click()} />}
            {isLoading && <ChatMessage.Loading />}
            <div ref={chatEndRef} />
          </div>
        </div>
        <MessageInput 
            onSendMessage={handleSendMessage} 
            isLoading={isLoading} 
            hasFileAssociated={!!activeConversation?.fileName}
            onUploadClick={() => fileInputRef.current.click()} 
        />
      </main>
    </div>
  );
};

export default ChatInterface;
