import React, { useState, useRef, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, LineChart, Line } from 'recharts';
import { UploadCloud, Send, Bot, User, FileText, BarChart2, Loader2, Sun, Wind, Droplets } from 'lucide-react';

// --- Helper Components ---

const ChatBubble = ({ msg }) => {
  const isBot = msg.sender === 'bot';
  const colors = ["#8884d8", "#82ca9d", "#ffc658", "#ff8042", "#0088FE", "#00C49F"];

  return (
    <div className={`flex items-end gap-3 w-full ${isBot ? 'justify-start' : 'justify-end'}`}>
      {isBot && (
        <div className="w-10 h-10 rounded-full bg-indigo-500 flex items-center justify-center flex-shrink-0">
          <Bot className="text-white" />
        </div>
      )}
      <div className={`max-w-2xl p-4 rounded-2xl shadow-md transition-all duration-300 ${isBot ? 'bg-gray-700 text-white rounded-bl-none' : 'bg-blue-600 text-white rounded-br-none'}`}>
        <p className="whitespace-pre-wrap">{msg.text}</p>
        {msg.type === 'chart' && msg.chartData && (
          <div className="mt-4 p-4 bg-gray-800 rounded-lg w-[550px] h-[350px] text-white">
            <ResponsiveContainer width="100%" height="100%">
              <LineChart data={msg.chartData} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
                <CartesianGrid strokeDasharray="3 3" stroke="#4A5568" />
                <XAxis dataKey="name" stroke="#A0AEC0" />
                <YAxis stroke="#A0AEC0" />
                <Tooltip contentStyle={{ backgroundColor: '#1A202C', border: '1px solid #4A5568' }} />
                <Legend />
                {msg.dataKeys && msg.dataKeys.map((key, index) => (
                  <Line key={key} type="monotone" dataKey={key} stroke={colors[index % colors.length]} activeDot={{ r: 8 }} />
                ))}
              </LineChart>
            </ResponsiveContainer>
          </div>
        )}
      </div>
      {!isBot && (
        <div className="w-10 h-10 rounded-full bg-gray-600 flex items-center justify-center flex-shrink-0">
          <User className="text-white" />
        </div>
      )}
    </div>
  );
};

const LoadingBubble = () => (
  <div className="flex items-end gap-3 justify-start">
    <div className="w-10 h-10 rounded-full bg-indigo-500 flex items-center justify-center flex-shrink-0">
      <Bot className="text-white" />
    </div>
    <div className="max-w-xl p-4 rounded-2xl bg-gray-700 rounded-bl-none flex items-center space-x-3">
      <Loader2 className="animate-spin text-white" size={20} />
      <span className="text-sm text-gray-300">The bot is analyzing your data...</span>
    </div>
  </div>
);


// --- Main App Component ---

const App = () => {
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isLoading]);

  useEffect(() => {
    setMessages([{
      sender: 'bot',
      text: 'Hello! I am an intelligent assistant for data analysis. Please upload your CSV or Parquet file to begin.',
      type: 'text'
    }]);
  }, []);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setFileName(selectedFile.name);
      setMessages(prev => [...prev, {
        sender: 'bot',
        text: `Great! You've loaded "${selectedFile.name}". What insights would you like to uncover?`,
        type: 'text'
      }]);
    }
  };

  const handleSendMessage = async () => {
    if (!input.trim() || isLoading || !file) return;

    const userMessage = { sender: 'user', text: input, type: 'text' };
    setMessages(prev => [...prev, userMessage]);
    const currentInput = input;
    setInput('');
    setIsLoading(true);

    const formData = new FormData();
    formData.append('file', file);
    formData.append('query', currentInput);

    try {
      const response = await fetch('http://localhost:5001/api/chat', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const err = await response.json();
        throw new Error(err.error || `HTTP error! status: ${response.status}`);
      }

      const botResponse = await response.json();
      setMessages(prev => [...prev, botResponse]);

    } catch (error) {
      console.error("Error fetching from backend:", error);
      const errorResponse = {
        sender: 'bot',
        text: `Sorry, an error occurred: ${error.message}. Please ensure the backend server is running correctly.`,
        type: 'text'
      };
      setMessages(prev => [...prev, errorResponse]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSampleQuery = (query) => {
    setInput(query);
  }

  return (
    <div className="flex h-screen bg-gray-800 text-gray-100 font-sans antialiased">
      {/* Sidebar */}
      <div className="w-[320px] bg-gray-900 p-6 flex flex-col border-r border-gray-700 shadow-xl">
        <div className="flex items-center gap-3 mb-8">
          <div className="p-2 bg-indigo-600 rounded-lg">
            <BarChart2 className="text-white" />
          </div>
          <h1 className="text-xl font-bold text-white">Data Insights Bot</h1>
        </div>

        <label htmlFor="file-upload" className="w-full cursor-pointer bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-3 px-4 rounded-lg inline-flex items-center justify-center transition-colors mb-4 shadow-md">
          <UploadCloud className="mr-2" />
          <span>{fileName ? 'Upload New File' : 'Upload Data File'}</span>
        </label>
        <input id="file-upload" type="file" className="hidden" onChange={handleFileChange} accept=".csv,.parquet" />

        {fileName && (
          <div className="mt-2 mb-6 p-3 bg-green-900/50 border border-green-700 rounded-lg text-center animate-fade-in">
            <div className="flex items-center text-green-300">
              <FileText size={18} className="mr-2 flex-shrink-0" />
              <span className="font-medium text-sm truncate">{fileName}</span>
            </div>
          </div>
        )}

        <div className="flex-grow">
            <h2 className="text-sm font-semibold text-gray-400 mb-3">SAMPLE QUERIES</h2>
            <div className="space-y-2">
                <button onClick={() => handleSampleQuery("Describe the data for me.")} className="w-full text-left text-sm text-gray-300 hover:text-white hover:bg-gray-700/50 p-2 rounded-md transition-colors">Describe the data</button>
                <button onClick={() => handleSampleQuery("What is the range of the max temperature?")} className="w-full text-left text-sm text-gray-300 hover:text-white hover:bg-gray-700/50 p-2 rounded-md transition-colors">Find temperature range</button>
                <button onClick={() => handleSampleQuery("Visualize the max and min temperature over time as a line chart.")} className="w-full text-left text-sm text-gray-300 hover:text-white hover:bg-gray-700/50 p-2 rounded-md transition-colors">Visualize temperatures</button>
            </div>
        </div>

        <div className="text-xs text-gray-500 mt-auto text-center">
          <p>Powered by Gemini</p>
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="flex-1 flex flex-col bg-gray-800">
        <div className="flex-1 p-6 overflow-y-auto">
          <div className="flex flex-col space-y-5">
            {messages.map((msg, index) => <ChatBubble key={index} msg={msg} />)}
            {isLoading && <LoadingBubble />}
            <div ref={chatEndRef} />
          </div>
        </div>

        {/* Message Input */}
        <div className="p-6 border-t border-gray-700 bg-gray-800">
          <div className="flex items-center bg-gray-900 rounded-xl p-2 shadow-inner">
            <input
              type="text"
              className="flex-1 bg-transparent border-none focus:ring-0 text-white placeholder-gray-500 px-4"
              placeholder={file ? "Ask about your data..." : "Please upload a file to begin"}
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
              disabled={!file || isLoading}
            />
            <button
              onClick={handleSendMessage}
              className="bg-indigo-600 hover:bg-indigo-500 disabled:bg-indigo-900/50 disabled:cursor-not-allowed text-white font-bold p-3 rounded-lg transition-colors"
              disabled={!file || isLoading || !input.trim()}
            >
              <Send />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
