import React from 'react';
import { LineChart, Line, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { useTheme } from '../contexts/ThemeContext';

const Chart = ({ chartData, dataKeys, chartType = 'line' }) => { // Default to 'line'
    const { theme } = useTheme();
    const colors = theme === 'dark' 
        ? ["#8884d8", "#82ca9d", "#ffc658", "#ff8042", "#00C49F"]
        : ["#5A67D8", "#38A169", "#D69E2E", "#DD6B20", "#3182CE"];

    const ChartComponent = chartType === 'bar' ? BarChart : LineChart;

    return (
        <div className="mt-4 p-4 bg-white dark:bg-gray-950 border border-gray-200 dark:border-gray-700 rounded-lg w-full h-[350px] animate-fade-in">
            <ResponsiveContainer width="100%" height="100%">
                <ChartComponent data={chartData} margin={{ top: 5, right: 20, left: 10, bottom: 5 }}>
                    <CartesianGrid strokeDasharray="3 3" stroke={theme === 'dark' ? "#4A5568" : "#E2E8F0"} />
                    <XAxis dataKey="name" stroke={theme === 'dark' ? "#A0AEC0" : "#4A5568"} tick={{ fill: theme === 'dark' ? '#A0AEC0' : '#4A5568', fontSize: 12 }} />
                    <YAxis stroke={theme === 'dark' ? "#A0AEC0" : "#4A5568"} tick={{ fill: theme === 'dark' ? '#A0AEC0' : '#4A5568', fontSize: 12 }} width={80} />
                    <Tooltip 
                        contentStyle={{ 
                            backgroundColor: theme === 'dark' ? 'rgba(31, 41, 55, 0.8)' : 'rgba(255, 255, 255, 0.8)', 
                            border: `1px solid ${theme === 'dark' ? '#4A5568' : '#E2E8F0'}`, 
                            backdropFilter: 'blur(4px)' 
                        }} 
                        labelStyle={{ color: theme === 'dark' ? '#E5E7EB' : '#1A202C' }}
                    />
                    <Legend wrapperStyle={{ fontSize: "14px" }} />
                    {chartType === 'bar' 
                        ? dataKeys.map((key, index) => <Bar key={key} dataKey={key} fill={colors[index % colors.length]} />)
                        : dataKeys.map((key, index) => <Line key={key} type="monotone" dataKey={key} stroke={colors[index % colors.length]} strokeWidth={2} activeDot={{ r: 8 }} />)
                    }
                </ChartComponent>
            </ResponsiveContainer>
        </div>
    );
};

export default Chart;
