import React, { useState, useRef, useEffect } from 'react';
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';
import {
  UploadCloud, Send, Bot, User, FileText, Loader2, Sparkles
} from 'lucide-react';

const ChatMessage = ({ msg }) => {
  const isBot = msg.sender === 'bot';
  const colors = ["#6366F1", "#10B981", "#F59E0B", "#EF4444", "#3B82F6"];

  return (
    <div className="flex gap-3">
      <div className="w-8 h-8 rounded-full bg-indigo-600 flex items-center justify-center text-white">
        {isBot ? <Bot size={16} /> : <User size={16} />}
      </div>
      <div className="flex-1 text-sm text-gray-200">
        <p className="font-semibold mb-1">{isBot ? 'Assistant' : 'You'}</p>
        <p className="whitespace-pre-wrap leading-relaxed">{msg.text}</p>
        {msg.type === 'chart' && msg.chartData && (
          <div className="mt-4 bg-gray-800 border border-gray-700 rounded-md p-4 h-64">
            <ResponsiveContainer width="100%" height="100%">
              <LineChart data={msg.chartData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#4B5563" />
                <XAxis dataKey="name" stroke="#9CA3AF" />
                <YAxis stroke="#9CA3AF" />
                <Tooltip
                  contentStyle={{ backgroundColor: '#1F2937', borderColor: '#4B5563' }}
                  labelStyle={{ color: '#F3F4F6' }}
                />
                <Legend />
                {msg.dataKeys.map((key, i) => (
                  <Line key={key} type="monotone" dataKey={key} stroke={colors[i % colors.length]} strokeWidth={2} />
                ))}
              </LineChart>
            </ResponsiveContainer>
          </div>
        )}
      </div>
    </div>
  );
};

const LoadingIndicator = () => (
  <div className="flex gap-3 items-center text-gray-400 text-sm">
    <div className="w-8 h-8 rounded-full bg-indigo-600 flex items-center justify-center text-white">
      <Bot size={16} />
    </div>
    <div className="flex items-center gap-2">
      <Loader2 className="animate-spin" size={16} />
      <span>Thinking...</span>
    </div>
  </div>
);

const App = () => {
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const chatEndRef = useRef(null);
  const fileInputRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isLoading]);

  useEffect(() => {
    if (messages.length === 0) {
      setMessages([{
        sender: 'bot',
        text: 'Hi! Upload a CSV or Parquet file to get started.',
        type: 'text'
      }]);
    }
  }, [messages.length]);

  const handleFileChange = (e) => {
    const selected = e.target.files[0];
    if (selected) {
      setFile(selected);
      setFileName(selected.name);
      setMessages(prev => [...prev, {
        sender: 'bot',
        text: `Loaded file: "${selected.name}". What would you like to do with it?`,
        type: 'text'
      }]);
    }
  };

  const handleSendMessage = async () => {
    if (!input.trim() || isLoading || !file) return;
    const msg = { sender: 'user', text: input, type: 'text' };
    setMessages(prev => [...prev, msg]);
    const currentInput = input;
    setInput('');
    setIsLoading(true);

    const formData = new FormData();
    formData.append('file', file);
    formData.append('query', currentInput);

    try {
      const res = await fetch('http://localhost:5001/api/chat', { method: 'POST', body: formData });
      const botResponse = await res.json();
      setMessages(prev => [...prev, botResponse]);
    } catch (err) {
      setMessages(prev => [...prev, { sender: 'bot', text: 'Something went wrong. Please try again.', type: 'text' }]);
    } finally {
      setIsLoading(false);
    }
  };

  const WelcomeScreen = () => (
    <div className="flex flex-col items-center justify-center text-center mt-20">
      <div className="p-3 bg-indigo-600 rounded-full mb-4">
        <Sparkles className="text-white" size={28} />
      </div>
      <h2 className="text-2xl font-bold text-gray-100">Welcome to Data Assistant</h2>
      <p className="text-gray-400 mt-2">Upload a file to get started.</p>
      <button onClick={() => fileInputRef.current?.click()} className="mt-6 px-4 py-2 bg-indigo-500 text-white rounded hover:bg-indigo-400">
        <UploadCloud size={16} className="inline mr-2" /> Select File
      </button>
    </div>
  );

  return (
    <div className="flex flex-col h-screen bg-gray-950">
      <input
        ref={fileInputRef}
        type="file"
        className="hidden"
        onChange={handleFileChange}
        accept=".csv,.parquet"
      />

      <header className="p-4 border-b border-gray-800 flex items-center justify-between">
        <h1 className="text-lg text-white font-semibold">Data Assistant</h1>
        {fileName && (
          <div className="text-sm text-gray-400 flex items-center gap-2">
            <FileText size={14} />
            {fileName}
          </div>
        )}
      </header>

      <main className="flex-1 overflow-y-auto p-4 space-y-6">
        {messages.length <= 1 && !file ? <WelcomeScreen /> : messages.map((msg, i) => <ChatMessage key={i} msg={msg} />)}
        {isLoading && <LoadingIndicator />}
        <div ref={chatEndRef} />
      </main>

      <footer className="p-4 border-t border-gray-800">
        <div className="flex items-center gap-2">
          <textarea
            className="flex-1 p-2 rounded bg-gray-800 text-white resize-none focus:outline-none"
            rows={1}
            placeholder={file ? 'Ask a question about your data...' : 'Upload a file to begin'}
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && !e.shiftKey && (e.preventDefault(), handleSendMessage())}
            disabled={!file || isLoading}
          />
          <button
            onClick={handleSendMessage}
            disabled={!file || isLoading || !input.trim()}
            className="p-2 bg-indigo-500 hover:bg-indigo-400 text-white rounded disabled:bg-gray-600"
          >
            <Send size={16} />
          </button>
        </div>
        <p className="text-xs text-center text-gray-500 mt-2">May occasionally make mistakes. Please verify important results.</p>
      </footer>
    </div>
  );
};

export default App;
