import React from 'react';
import { Plus, Trash2, X } from 'lucide-react';

const Sidebar = ({
  isOpen,
  setIsOpen,
  conversations,
  activeConversationId,
  setActiveConversationId,
  createNewConversation,
  deleteConversation,
}) => (
  <>
    {/* Overlay for mobile, appears when sidebar is open */}
    <div
      className={`fixed inset-0 z-40 bg-black/50 transition-opacity md:hidden ${isOpen ? 'opacity-100 visible' : 'opacity-0 invisible'}`}
      onClick={() => setIsOpen(false)}
    />
    {/* Sidebar panel */}
    <aside
      className={`
        fixed top-0 left-0 z-50 h-full w-72 bg-gray-100 dark:bg-gray-900 border-r border-gray-200 dark:border-gray-800 
        flex flex-col shadow-xl transition-transform duration-300 ease-in-out
        ${isOpen ? 'translate-x-0' : '-translate-x-full'} 
      `}
    >
      <div className="flex items-center justify-between p-4 border-b border-gray-200 dark:border-gray-700 flex-shrink-0">
        <span className="text-lg font-bold">Conversations</span>
        <div className="flex items-center gap-2">
          <button
            className="p-2 rounded-md hover:bg-gray-300 dark:hover:bg-gray-700"
            onClick={createNewConversation}
            title="New Conversation"
          >
            <Plus size={20} />
          </button>
          <button
            className="p-2 rounded-md hover:bg-gray-300 dark:hover:bg-gray-700"
            onClick={() => setIsOpen(false)}
            title="Close"
          >
            <X size={20} />
          </button>
        </div>
      </div>
      <div className="flex-1 overflow-y-auto p-2 space-y-1">
        {Object.values(conversations)
            .sort((a, b) => (b.lastModified || 0) - (a.lastModified || 0)) // Sort by most recent
            .map(conv => (
          <div
            key={conv.id}
            className={`group flex items-center justify-between p-3 rounded-lg cursor-pointer transition-colors
              ${conv.id === activeConversationId 
                ? 'bg-blue-500 text-white font-semibold' 
                : 'hover:bg-gray-200 dark:hover:bg-gray-800'
              }`}
            onClick={() => setActiveConversationId(conv.id)}
          >
            <div className="truncate pr-2" title={conv.title}>
              {conv.title}
            </div>
            <button
              onClick={(e) => {
                e.stopPropagation(); // Prevent setting active conversation
                if (window.confirm(`Are you sure you want to delete "${conv.title}"?`)) {
                    deleteConversation(conv.id);
                }
              }}
              title="Delete Conversation"
              className={`ml-2 text-gray-500 hover:text-red-500 opacity-0 group-hover:opacity-100 transition-opacity
                ${conv.id === activeConversationId ? 'text-blue-200 hover:text-white' : ''}
              `}
            >
              <Trash2 size={16} />
            </button>
          </div>
        ))}
      </div>
    </aside>
  </>
);

export default Sidebar;
