import React, { useState, useRef, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { UploadCloud, Send, Bot, FileText, BarChart2, Loader2 } from 'lucide-react';

// Main App Component
const App = () => {
  // State management
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [chartData, setChartData] = useState(null);
  const chatEndRef = useRef(null);

  // Effect to scroll to the latest message
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Initial greeting message
  useEffect(() => {
    setMessages([{
      sender: 'bot',
      text: 'Hello! Please upload your CSV or Parquet file to begin.',
      type: 'text'
    }]);
  }, []);

  // Handler for file selection
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setFileName(selectedFile.name);
      setMessages(prev => [...prev, {
        sender: 'bot',
        text: `Great! You've loaded "${selectedFile.name}". What insights are you looking for?`,
        type: 'text'
      }]);
      setChartData(null); // Clear previous chart data
    }
  };

  // Handler for sending a message
  const handleSendMessage = async () => {
    if (!input.trim() || isLoading || !file) return;

    const userMessage = { sender: 'user', text: input, type: 'text' };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);
    setChartData(null); // Clear chart on new query

    // --- REAL AI/Backend Logic ---
    // Create a FormData object to send the file and the query
    const formData = new FormData();
    formData.append('file', file);
    formData.append('query', input);

    try {
      // Fetch response from the backend server
      const response = await fetch('http://localhost:5001/api/chat', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const botResponse = await response.json();

      // Update messages with the bot's response
      setMessages(prev => [...prev, botResponse]);

      // If the response contains chart data, set it for rendering
      if (botResponse.type === 'chart' && botResponse.chartData) {
        setChartData(botResponse.chartData);
      }

    } catch (error) {
      console.error("Error fetching from backend:", error);
      const errorResponse = {
        sender: 'bot',
        text: "Sorry, I couldn't connect to the backend. Please ensure the server is running.",
        type: 'text'
      };
      setMessages(prev => [...prev, errorResponse]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="flex h-screen bg-gray-900 text-white font-sans">
      {/* Sidebar for File Upload and Info */}
      <div className="w-1/4 bg-gray-950 p-6 flex flex-col justify-between border-r border-gray-800">
        <div>
          <h1 className="text-2xl font-bold text-indigo-400 mb-6 flex items-center">
            <BarChart2 className="mr-3" />
            Data Insights Bot
          </h1>
          <p className="text-gray-400 mb-4 text-sm">
            Upload your data file and ask questions to get insights and visualizations.
          </p>
          <label htmlFor="file-upload" className="w-full cursor-pointer bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-3 px-4 rounded-lg inline-flex items-center justify-center transition-colors">
            <UploadCloud className="mr-2" />
            <span>Upload File</span>
          </label>
          <input id="file-upload" type="file" className="hidden" onChange={handleFileChange} accept=".csv,.parquet" />

          {fileName && (
            <div className="mt-6 p-4 bg-gray-800 rounded-lg">
              <div className="flex items-center text-green-400">
                <FileText size={20} className="mr-3 flex-shrink-0" />
                <span className="font-medium truncate">{fileName}</span>
              </div>
              <p className="text-xs text-gray-500 mt-2">File loaded successfully.</p>
            </div>
          )}
        </div>
        <div className="text-xs text-gray-600">
          <p>&copy; 2025 AI Data Assistant</p>
          <p>Powered by Gemini</p>
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="w-3/4 flex flex-col bg-gray-900">
        <div className="flex-1 p-6 overflow-y-auto">
          <div className="flex flex-col space-y-4">
            {messages.map((msg, index) => (
              <div key={index} className={`flex items-end gap-3 ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
                {msg.sender === 'bot' && <div className="w-10 h-10 rounded-full bg-indigo-500 flex items-center justify-center flex-shrink-0"><Bot /></div>}

                <div className={`max-w-xl p-4 rounded-2xl ${msg.sender === 'user' ? 'bg-blue-600 rounded-br-none' : 'bg-gray-800 rounded-bl-none'}`}>
                  <p className="whitespace-pre-wrap">{msg.text}</p>
                  {msg.type === 'chart' && chartData && (
                    <div className="mt-4 p-4 bg-gray-900 rounded-lg w-[500px] h-[300px]">
                      <ResponsiveContainer width="100%" height="100%">
                        <BarChart data={chartData}>
                          <CartesianGrid strokeDasharray="3 3" stroke="#4A5568" />
                          <XAxis dataKey="name" stroke="#A0AEC0" />
                          <YAxis stroke="#A0AEC0" />
                          <Tooltip contentStyle={{ backgroundColor: '#1A202C', border: '1px solid #4A5568' }} />
                          <Legend />
                          <Bar dataKey="value" fill="#818CF8" />
                        </BarChart>
                      </ResponsiveContainer>
                    </div>
                  )}
                </div>
              </div>
            ))}
            {isLoading && (
              <div className="flex items-end gap-3 justify-start">
                <div className="w-10 h-10 rounded-full bg-indigo-500 flex items-center justify-center flex-shrink-0"><Bot /></div>
                <div className="max-w-xl p-4 rounded-2xl bg-gray-800 rounded-bl-none flex items-center space-x-2">
                   <Loader2 className="animate-spin" size={20} />
                   <span>Bot is thinking...</span>
                </div>
              </div>
            )}
            <div ref={chatEndRef} />
          </div>
        </div>

        {/* Message Input */}
        <div className="p-6 border-t border-gray-800">
          <div className="flex items-center bg-gray-800 rounded-lg p-2">
            <input
              type="text"
              className="flex-1 bg-transparent border-none focus:ring-0 text-white placeholder-gray-500 px-4"
              placeholder={file ? "Ask about your data..." : "Please upload a file first"}
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
              disabled={!file || isLoading}
            />
            <button
              onClick={handleSendMessage}
              className="bg-indigo-600 hover:bg-indigo-700 disabled:bg-indigo-900 disabled:cursor-not-allowed text-white font-bold p-3 rounded-md transition-colors"
              disabled={!file || isLoading || !input.trim()}
            >
              <Send />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
