import React from 'react';
import { Trash2, X } from 'lucide-react';

const Sidebar = ({
  isOpen,
  setIsOpen,
  conversations,
  activeConversationId,
  setActiveConversationId,
  createNewConversation,
  deleteConversation,
}) => (
  <>
    {/* Overlay for mobile */}
    <div
      className={`fixed inset-0 z-30 bg-black/40 transition-opacity md:hidden ${isOpen ? 'block' : 'hidden'}`}
      onClick={() => setIsOpen(false)}
    />
    <aside className={`fixed md:static top-0 left-0 z-40 h-full bg-gray-200 dark:bg-gray-800 border-r dark:border-gray-900 flex flex-col duration-300 transition-all w-72
      ${isOpen ? 'block' : 'hidden'} md:block
    `}>
      <div className="flex items-center justify-between px-4 py-4 border-b border-gray-300 dark:border-gray-700">
        <span className="text-lg font-bold">Conversations</span>
        <div className="flex items-center gap-2">
          <button
            className="p-1 rounded hover:bg-gray-300 dark:hover:bg-gray-700"
            onClick={createNewConversation}
            title="New Conversation"
            tabIndex={0}
          >+</button>
          <button
            className="md:hidden"
            onClick={() => setIsOpen(false)}
            title="Close"
            tabIndex={0}
          ><X size={20}/></button>
        </div>
      </div>
      <div className="flex-1 overflow-y-auto">
        {Object.values(conversations).map((conv) => (
          <div key={conv.id} className="flex items-center justify-between px-4 py-2 hover:bg-gray-300/50 dark:hover:bg-gray-700/50 rounded-lg group">
            <div
              className={`truncate cursor-pointer ${conv.id === activeConversationId ? 'font-semibold text-blue-700 dark:text-blue-300' : ''}`}
              onClick={() => { setActiveConversationId(conv.id); setIsOpen(false); }}
              title={conv.title}
            >
              {conv.title}
            </div>
            <button
              className="ml-2 text-red-500 opacity-0 group-hover:opacity-100 transition-opacity"
              onClick={() => deleteConversation(conv.id)}
              title="Delete chat"
              tabIndex={-1}
            >
              <Trash2 size={16}/>
            </button>
          </div>
        ))}
      </div>
    </aside>
  </>
);

export default Sidebar;
