import React from 'react';
import { History, Sun, Moon, FileText } from 'lucide-react';

const Header = ({ title, fileName, onHistoryClick, onThemeToggle, theme }) => (
    <header className="w-full max-w-4xl p-4 flex justify-between items-center">
        <button onClick={onHistoryClick} className="p-2 rounded-md hover:bg-gray-200 dark:hover:bg-gray-700">
            <History size={20} />
        </button>
        <div className="text-center">
            <h1 className="text-lg font-semibold text-gray-600 dark:text-gray-300">
                {title || 'Fashion AI'}
            </h1>
            {fileName && (
                <div className="text-xs text-gray-500 dark:text-gray-400 flex items-center justify-center gap-1">
                    <FileText size={12} />
                    <span>{fileName}</span>
                </div>
            )}
        </div>
        <button onClick={onThemeToggle} className="p-2 rounded-md hover:bg-gray-200 dark:hover:bg-gray-700">
            {theme === 'dark' ? <Sun size={20} /> : <Moon size={20} />}
        </button>
    </header>
);

export default Header;
